SELECT 
    'Проверка соответствий' as status,
    COUNT(*) as total_backup_records,
    COUNT(CASE WHEN pe_new.id IS NOT NULL THEN 1 END) as can_restore_count,
    COUNT(CASE WHEN pe_new.id IS NULL THEN 1 END) as cannot_restore_count
FROM workout_sets_backup ws_backup
INNER JOIN workouts w ON w.id = ws_backup.workout_id
INNER JOIN plans p ON p.id = w.plan_id
INNER JOIN cycles c ON c.id = p.cycle_id
INNER JOIN plan_exercises_backup pe_old_backup ON pe_old_backup.id = ws_backup.plan_exercise_id_old
LEFT JOIN plan_exercises pe_new ON pe_new.plan_id = w.plan_id 
    AND pe_new.exercise_id = pe_old_backup.exercise_id
WHERE ws_backup.workout_id IN (87, 91, 100, 101, 102, 103, 104, 105, 106, 107, 108, 109, 110, 111, 112, 113, 114, 115, 116, 117, 118, 119, 120)
    AND c.user_id = 1  -- ЗАМЕНИТЕ НА ВАШ USER_ID
    AND c.end_date IS NULL;

-- ============================================
-- ШАГ 5: ВОССТАНОВЛЕНИЕ workout_sets с обновленными plan_exercise_id
-- ============================================
SELECT 
    ws_backup.workout_id,
    pe_new.id as plan_exercise_id,  -- Новый plan_exercise_id
    ws_backup.weight,
    ws_backup.reps,
    ws_backup.created_at,  -- Сохраняем исходную дату
    ws_backup.updated_at   -- Сохраняем исходную дату
FROM workout_sets_backup ws_backup
INNER JOIN workouts w ON w.id = ws_backup.workout_id
INNER JOIN plans p ON p.id = w.plan_id
INNER JOIN cycles c ON c.id = p.cycle_id
INNER JOIN plan_exercises_backup pe_old_backup ON pe_old_backup.id = ws_backup.plan_exercise_id_old
INNER JOIN plan_exercises pe_new ON pe_new.plan_id = w.plan_id 
    AND pe_new.exercise_id = pe_old_backup.exercise_id
WHERE ws_backup.workout_id IN (87, 91, 100, 101, 102, 103, 104, 105, 106, 107, 108, 109, 110, 111, 112, 113, 114, 115, 116, 117, 118, 119, 120)
    AND c.user_id = 1  -- ЗАМЕНИТЕ НА ВАШ USER_ID
    AND c.end_date IS NULL  -- Активный цикл
    -- Проверка на дубликаты (по workout_id + plan_exercise_id + weight + reps)
    AND NOT EXISTS (
        SELECT 1 
        FROM workout_sets ws_existing
        WHERE ws_existing.workout_id = ws_backup.workout_id
            AND ws_existing.plan_exercise_id = pe_new.id
            AND ws_existing.weight = ws_backup.weight
            AND ws_existing.reps = ws_backup.reps
    )
    AND pe_new.id IS NOT NULL;

-- ============================================
-- ШАГ 6: Проверка результата
-- ============================================
SELECT 
    'Восстановлено записей' as status,
    COUNT(*) as count
FROM workout_sets 
WHERE updated_at >= DATE_SUB(NOW(), INTERVAL 5 MINUTE);

-- Статистика по планам
SELECT 
    p.id as plan_id,
    p.name as plan_name,
    COUNT(DISTINCT w.id) as workouts_count,
    COUNT(ws.id) as workout_sets_count
FROM plans p
INNER JOIN cycles c ON c.id = p.cycle_id
LEFT JOIN workouts w ON w.plan_id = p.id
LEFT JOIN workout_sets ws ON ws.workout_id = w.id
WHERE c.user_id = 1  -- ЗАМЕНИТЕ НА ВАШ USER_ID
    AND c.end_date IS NULL
GROUP BY p.id, p.name
ORDER BY p.updated_at DESC;

